// O n8n passa os dados dentro de um array de objetos chamado 'items'
for (const item of items) {
  // Acessa o caminho espec√≠fico do seu JSON
  const relatorio = item.json.output.relatorio_geral;
  const analise = relatorio.analise_detalhada;

  // Filtra o array contando apenas onde o status √© 'outlier'
  const contagem = analise.filter(cliente => cliente.status === 'outlier').length;

  // Adiciona a nova chave. 
  // Nota: Objetos JS n√£o garantem ordem visual, mas ao converter para JSON 
  // ele aparecer√° no corpo do objeto.
  relatorio.quantidade_outliers = contagem.toString();
}

return items;

const input = items[0].json;

// 1. Calcular a contagem de outliers para a Badge
const totalOutliers = input.analise_detalhada.filter(item => item.status === 'outlier').length;

// 2. Fun√ß√£o para gerar os √∫ltimos 12 meses cronologicamente
const gerarLabelsCronologicos = (dataRef) => {
    const mesesNomes = ["Jan", "Fev", "Mar", "Abr", "Mai", "Jun", "Jul", "Ago", "Set", "Out", "Nov", "Dez"];
    const labels = [];
    const chavesBusca = [];

    let dataAtual = new Date(dataRef);

    for (let i = 11; i >= 0; i--) {
        let d = new Date(dataAtual.getFullYear(), dataAtual.getMonth() - i, 1);
        labels.push(`${mesesNomes[d.getMonth()]}/${d.getFullYear().toString().substring(2)}`);

        const ano = d.getFullYear();
        const mes = (d.getMonth() + 1).toString().padStart(2, '0');
        chavesBusca.push(`${ano}-${mes}`);
    }
    return { labels, chavesBusca };
};

const { labels: labelsGrafico, chavesBusca } = gerarLabelsCronologicos(input.data_referencia);

const rawData = input.analise_detalhada.map((item, index) => {
    const contrato = item.historico_contratos?.[0] || {};
    const readings = contrato.readings || {};
    const finalValues = chavesBusca.map(chave => {
        const dataEncontrada = Object.keys(readings).find(k => k.startsWith(chave));
        return dataEncontrada ? readings[dataEncontrada] : 0;
    });

    return {
        ...item,
        contractId: contrato.contract_id || "N/A",
        isActive: contrato.is_active === true,
        chartLabels: labelsGrafico,
        chartValues: finalValues,
        originalIndex: index,
        selected: false 
    };
});

const htmlContent = `
<!DOCTYPE html>
<html lang="pt-br">
<head>
    <meta charset="UTF-8">
    <title>Dashboard de Consumo</title>
    <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/html2pdf.js/0.10.1/html2pdf.bundle.min.js"></script>
    <style>
        :root {
            --bg-body: #f0f2f5;
            --bg-card: #ffffff;
            --text-main: #333333;
            --text-secondary: #5f6368;
            --border-color: #e0e0e0;
            --row-hover: #f1f8ff;
            --table-header: #f8f9fa;
            --btn-secondary: #f8f9fa;
            --input-bg: #ffffff;
        }

        [data-theme="dark"] {
            --bg-body: #121212;
            --bg-card: #1e1e1e;
            --text-main: #e0e0e0;
            --text-secondary: #aaaaaa;
            --border-color: #333333;
            --row-hover: #2c2c2c;
            --table-header: #252525;
            --btn-secondary: #333333;
            --input-bg: #252525;
        }

        body { font-family: 'Segoe UI', Tahoma, sans-serif; background: var(--bg-body); padding: 20px; color: var(--text-main); transition: background 0.3s, color 0.3s; }
        .card { background: var(--bg-card); padding: 25px; border-radius: 12px; box-shadow: 0 4px 15px rgba(0,0,0,0.2); max-width: 1200px; margin: auto; position: relative; }

        .header-container { display: flex; justify-content: space-between; align-items: flex-start; margin-bottom: 10px; }
        .header-left h1 { color: #1a73e8; margin: 0; }

        .theme-toggle {
            cursor: pointer; background: var(--btn-secondary); border: 1px solid var(--border-color);
            padding: 8px 12px; border-radius: 8px; color: var(--text-main); font-weight: bold;
            display: flex; align-items: center; gap: 8px; transition: 0.2s;
        }

        .outlier-count-badge {
            background: #d93025; color: white; padding: 8px 16px; border-radius: 50px;
            font-weight: bold; font-size: 0.9em; display: flex; align-items: center; gap: 8px;
        }

        .ref-date { color: var(--text-secondary); margin: 5px 0 20px 0; font-size: 0.95em; }

        .controls-row { display: flex; gap: 15px; margin-bottom: 10px; align-items: center; }
        .search-container { flex-grow: 1; }
        #searchInput {
            width: 100%; padding: 12px 15px; border: 2px solid var(--border-color); border-radius: 8px;
            font-size: 1em; outline: none; transition: 0.3s; box-sizing: border-box;
            background: var(--input-bg); color: var(--text-main);
        }

        /* Estilo dos Filtros por Status */
        .filter-container { display: flex; gap: 10px; margin-bottom: 20px; flex-wrap: wrap; }
        .filter-chip {
            padding: 6px 14px; border-radius: 20px; border: 1px solid var(--border-color);
            background: var(--bg-card); color: var(--text-secondary); cursor: pointer;
            font-size: 0.85em; font-weight: 600; transition: 0.2s;
        }
        .filter-chip:hover { background: var(--row-hover); }
        .filter-chip.active { background: #1a73e8; color: white; border-color: #1a73e8; }

        .btn-export { 
            padding: 10px 18px; border-radius: 8px; border: none; cursor: pointer; 
            font-weight: 600; font-size: 0.9em; display: flex; align-items: center; gap: 8px;
            transition: all 0.2s; background: var(--btn-secondary); color: var(--text-main); border: 1px solid var(--border-color);
        }
        .btn-pdf { background: #fce8e6; color: #d93025; border: none; }
        .btn-csv { background: #e6f4ea; color: #1e8e3e; border: none; }

        table { width: 100%; border-collapse: collapse; }
        th { background: var(--table-header); color: var(--text-secondary); text-transform: uppercase; font-size: 0.75em; padding: 15px; text-align: left; }
        td { padding: 15px; border-bottom: 1px solid var(--border-color); }

        /* Cores dos Badges de Status */
        .status-badge { padding: 4px 10px; border-radius: 20px; font-size: 0.7em; font-weight: bold; }
        .status-normal { background: #e6f4ea; color: #1e8e3e; }
        .status-outlier { background: #fce8e6; color: #d93025; }
        .status-no-data { background: #fef7e0; color: #b05a00; } /* AMARELO para no recent data */

        .details-content { padding: 20px; border-left: 5px solid #1a73e8; background: var(--table-header); margin: 10px; border-radius: 4px; }
        .chart-container { height: 250px; background: var(--bg-card); padding: 10px; border-radius: 8px; border: 1px solid var(--border-color); }
    </style>
</head>
<body>
    <div class="card" id="dashboard-content">
        <div class="header-container">
            <div class="header-left">
                <h1>Dashboard de Gest√£o Energ√©tica</h1>
                <p class="ref-date">M√™s de An√°lise: <strong>${input.data_referencia}</strong></p>
            </div>
            <div style="display: flex; gap: 10px;">
                <button class="theme-toggle" onclick="toggleTheme()" id="themeBtn">üåô Dark Mode</button>
                ${totalOutliers > 0 ? `<div class="outlier-count-badge"><span>‚ö†Ô∏è</span><span>${totalOutliers} Outliers</span></div>` : ''}
            </div>
        </div>

        <div class="controls-row">
            <div class="search-container">
                <input type="text" id="searchInput" onkeyup="renderTable()" placeholder="üîç Pesquisar por nome do cliente...">
            </div>
            <button class="btn-export btn-csv" onclick="exportCSV()" id="btnCsv"><span>üìä</span> Exportar CSV</button>
            <button class="btn-export btn-pdf" onclick="exportPDF()" id="btnPdf"><span>üìÑ</span> Gerar PDF</button>
        </div>

        <div class="filter-container" id="filterContainer">
            <div class="filter-chip active" onclick="setFilter('all', this)">Todos</div>
            <div class="filter-chip" onclick="setFilter('normal', this)">Normal</div>
            <div class="filter-chip" onclick="setFilter('outlier', this)">Outlier</div>
            <div class="filter-chip" onclick="setFilter('no recent data', this)">Sem Dados Recentes</div>
        </div>

        <table id="main-table">
            <thead>
                <tr>
                    <th style="width: 40px;"><input type="checkbox" id="selectAll" onclick="toggleAll(this)"></th>
                    <th class="sortable" onclick="handleSort('cliente')">Cliente</th>
                    <th class="sortable" onclick="handleSort('consumo_medio_3m')">M√©dia (3m)</th>
                    <th class="sortable" onclick="handleSort('status')">Status</th>
                    <th>Observa√ß√£o T√©cnica</th>
                </tr>
            </thead>
            <tbody id="tableBody"></tbody>
        </table>
    </div>

    <script>
        let data = ${JSON.stringify(rawData)};
        let currentSort = { column: null, direction: 'none' };
        let currentFilter = 'all';
        const charts = {};

        function toggleTheme() {
            const body = document.body;
            const btn = document.getElementById('themeBtn');
            const isDark = body.getAttribute('data-theme') === 'dark';
            if (isDark) {
                body.removeAttribute('data-theme');
                btn.innerHTML = "üåô Dark Mode";
            } else {
                body.setAttribute('data-theme', 'dark');
                btn.innerHTML = "‚òÄÔ∏è Light Mode";
            }
            Object.keys(charts).forEach(index => {
                const row = document.getElementById('details-' + index);
                if (row && row.style.display !== 'none') renderChart(index);
            });
        }

        function setFilter(status, el) {
            currentFilter = status;
            document.querySelectorAll('.filter-chip').forEach(chip => chip.classList.remove('active'));
            el.classList.add('active');
            renderTable();
        }

        function toggleAll(masterCheckbox) {
            const visibleItems = getFilteredData();
            visibleItems.forEach(item => item.selected = masterCheckbox.checked);
            renderTable();
        }

        function toggleItem(index) {
            const item = data.find(d => d.originalIndex == index);
            if (item) item.selected = !item.selected;
            updateExportButtons();
        }

        function updateExportButtons() {
            const selectedCount = data.filter(d => d.selected).length;
            document.getElementById('btnCsv').disabled = selectedCount === 0;
            document.getElementById('btnPdf').disabled = selectedCount === 0;
            document.getElementById('btnCsv').innerHTML = \`<span>üìä</span> Exportar CSV (\${selectedCount})\`;
            document.getElementById('btnPdf').innerHTML = \`<span>üìÑ</span> Gerar PDF (\${selectedCount})\`;
        }

        function handleSort(column) {
            if (currentSort.column === column) {
                currentSort.direction = currentSort.direction === 'asc' ? 'desc' : (currentSort.direction === 'desc' ? 'none' : 'asc');
            } else {
                currentSort.column = column;
                currentSort.direction = 'asc';
            }
            renderTable();
        }

        function getFilteredData() {
            const searchTerm = document.getElementById('searchInput').value.toLowerCase();
            return data.filter(item => {
                const matchesSearch = item.cliente.toLowerCase().includes(searchTerm);
                const matchesFilter = currentFilter === 'all' || item.status === currentFilter;
                return matchesSearch && matchesFilter;
            });
        }

        function renderTable() {
            const tbody = document.getElementById('tableBody');
            let filtered = getFilteredData();

            if (currentSort.direction !== 'none') {
                filtered.sort((a, b) => {
                    let valA = a[currentSort.column];
                    let valB = b[currentSort.column];
                    if (currentSort.column === 'consumo_medio_3m') {
                        valA = parseFloat(valA) || 0;
                        valB = parseFloat(valB) || 0;
                    }
                    return currentSort.direction === 'asc' ? (valA > valB ? 1 : -1) : (valA < valB ? 1 : -1);
                });
            }

            tbody.innerHTML = filtered.map((item) => {
                let statusClass = 'status-normal';
                if(item.status === 'outlier') statusClass = 'status-outlier';
                if(item.status === 'no recent data') statusClass = 'status-no-data';

                return \`
                    <tr class="clickable-row">
                        <td><input type="checkbox" \${item.selected ? 'checked' : ''} onchange="toggleItem(\${item.originalIndex})"></td>
                        <td onclick="handleRowClick('\${item.originalIndex}')" style="cursor:pointer"><strong>\${item.cliente}</strong></td>
                        <td onclick="handleRowClick('\${item.originalIndex}')" style="cursor:pointer">\${item.consumo_medio_3m}</td>
                        <td onclick="handleRowClick('\${item.originalIndex}')" style="cursor:pointer"><span class="status-badge \${statusClass}">\${item.status}</span></td>
                        <td onclick="handleRowClick('\${item.originalIndex}')" style="cursor:pointer">\${item.observacao_tecnica}</td>
                    </tr>
                    <tr id="details-\${item.originalIndex}" style="display:none;" class="details-row">
                        <td colspan="5">
                            <div class="details-content">
                                <div style="display:flex; justify-content:space-between; margin-bottom:10px;">
                                    <p><strong>ID do Contrato:</strong> \${item.contractId}</p>
                                    <button class="theme-toggle" style="font-size: 0.8em;" onclick="copyChartToClipboard(\${item.originalIndex}, this)">üì∑ Copiar Gr√°fico</button>
                                </div>
                                <div class="chart-container">
                                    <canvas id="chart-\${item.originalIndex}"></canvas>
                                </div>
                            </div>
                        </td>
                    </tr>
                \`;
            }).join('');
            updateExportButtons();
        }

        function handleRowClick(index) {
            const row = document.getElementById('details-' + index);
            const isOpening = (row.style.display === 'none');
            if (isOpening) {
                row.style.display = 'table-row';
                renderChart(index);
            } else {
                row.style.display = 'none';
            }
        }

        function renderChart(index) {
            const isDark = document.body.getAttribute('data-theme') === 'dark';
            const gridColor = isDark ? '#333333' : '#e0e0e0';
            const textColor = isDark ? '#aaaaaa' : '#5f6368';
            const item = data.find(d => d.originalIndex == index);
            const ctx = document.getElementById('chart-' + index).getContext('2d');
            if (charts[index]) charts[index].destroy();
            charts[index] = new Chart(ctx, {
                type: 'line',
                data: {
                    labels: item.chartLabels,
                    datasets: [{ 
                        label: 'kWh', 
                        data: item.chartValues, 
                        borderColor: '#1a73e8', 
                        backgroundColor: 'rgba(26, 115, 232, 0.2)',
                        fill: true,
                        tension: 0.3
                    }]
                },
                options: {
                    responsive: true,
                    maintainAspectRatio: false,
                    scales: {
                        x: { grid: { color: gridColor }, ticks: { color: textColor } },
                        y: { grid: { color: gridColor }, ticks: { color: textColor } }
                    },
                    plugins: { legend: { labels: { color: textColor } } }
                }
            });
        }

        async function copyChartToClipboard(index, button) {
            const canvas = document.getElementById('chart-' + index);
            canvas.toBlob(async (blob) => {
                const item = new ClipboardItem({ "image/png": blob });
                await navigator.clipboard.write([item]);
                button.innerText = "‚úÖ Copiado!";
                setTimeout(() => button.innerText = "üì∑ Copiar Gr√°fico", 2000);
            });
        }

        function exportCSV() {
            const selected = data.filter(d => d.selected);
            const csv = "Cliente,Media,Status,Obs\\n" + selected.map(i => \`"\${i.cliente}",\${i.consumo_medio_3m},\${i.status},"\${i.observacao_tecnica}"\`).join("\\n");
            const blob = new Blob([csv], { type: 'text/csv' });
            const url = window.URL.createObjectURL(blob);
            const a = document.createElement('a'); a.href = url; a.download = 'relatorio_energia.csv'; a.click();
        }

        function exportPDF() {
            const element = document.getElementById('dashboard-content');
            html2pdf().from(element).save('gestao_energetica.pdf');
        }

        renderTable();
    </script>
</body>
</html>
`;

return [{ json: { html: htmlContent } }];
